import React, { Fragment, useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState('');
  const [shown, setShown] = useState({});


  // useEffect(() => {
  //   console.log('Effect');
  //   axios
  //   .get('https://restcountries.eu/rest/v2/all')
  //   //.get ('api.openweathermap.org/data/2.5/weather?q={city name},{country code}')
  //   .then(response => {
  //     console.log('Promise fulfiled', response);
  //     setData(response.data);
  //   }) 
  //   .catch(error => {
  //     console.log(error.response);
  //   });

  // }, []);

  useEffect(() => {
    // console.log('Effect');
    axios
    // .get(`https://restcountries.eu/rest/v2/capital/${search}`)
    .get(`https://restcountries.eu/rest/v2/name/${search}`)
    // .get(`https://restcountries.eu/rest/v2/currency/${search}`)
    .then(response => {
      console.log('Promise fulfiled', response);
      setData(response.data);
    }) 
    .catch(error => {
      console.log(error.response);
    });

  }, [search]);

  const handelSearchChanged = (e) => {
    e.preventDefault();
    setSearch(e.target.value);

    if (e.target.value.length < 4){
     //e.target.value.innerHTML = "max value 4";
      document.getElementById("text").style.visibility = "visible";
    }else{
      document.getElementById("text").style.visibility = "hidden";
    }
  }
  // function myFunction(){
  //   document.getElementById("name").maxLength = "4";
  //   document.getElementById("text").innerHTML = "max value 4";
  // // }
  // const myName = () => {
  //   let x = document.getElementById('hide');
  //   if(x.style.display === ''){

  //      x.style.display = 'block';
  //      setShown(shown);
  //   }else{
  //     x.style.display = 'none';
  //     setShown(shown);
  //   }
  // }

  const toggleShown = (name) => {
    setShown(prevShown => ({
      ...prevShown,
      [name]:!prevShown[name]
    }));
  }

  const labelStyle = {
    visibility: 'hidden',
    color: 'red'
  };
  return (
    <Fragment>
      Find Countries: <input type = 'text'  value = {search} onChange = {handelSearchChanged} placeholder = "Search Countries ..."/>
      {/* Find Countries: <input type = 'text' value = {search} onChange = {(e) => setSearch(e.target.value)} placeholder = "Search Countries ..."/> */}
      {/* <input type = "text" id = "text" onChange = "myFunction()" /> */}

      <label id = "text" style={labelStyle}>Your title is too long</label>

      {/* <button onClick = {() => setQuery(search)}>Search</button> */}


     <div>
     {data.map(item => (
        <ul key={item.name}>
          {/* <a href={item.url}>{item.capital}</a><br/> */}
          {item.name ? (<button id = "show" onClick ={() => toggleShown(item.name)}>Show</button>): null}<br/>

          {/* <button onClick = {() => setSearch(search)}>Search</button> */}
          {/* <button onClick = {(myName) => setQuery(query)}>Search</button> */}
          {/* <div id = "hide">
          Capital: {item.capital}<br/>
          Population: {item.population}<br/>
          Area: {item.area}<br/>
          Region: {item.region}<br/> */}
          {/* {item.currencies.code} */}
          {/* {item.languages.name}  */}
          {/* <img src = {item.flag} alt = "flag" width = "150" height = "90" border = "1px solid #000000" /> */}
          {/* </div> */}
          <div>{item.name}</div>
          {shown[item.name] ? 
          <p><h1>Country: {item.name}</h1>
          Capital:{item.capital}<br/>
          Population:{item.population}<br/>
          Area:{item.area}<br/>
          Region:{item.region}<br/>
          Languages:{item.languages.name}<br/>
          <img src = {item.flag} alt = "flag" width = "150" height = "90" border = "1px solid #000000" />
          </p>
          :null}
        </ul>
      ))}
      </div> 
    </Fragment>

  );
}
export default App;


