import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState('');
  useEffect(() => {
    console.log('Effect');
    axios
    .get('https://restcountries.eu/rest/v2/all', )
    .then(response => {
      console.log('Promise fulfiled');
      setData(response.data);
    }) 

  }, []);

  const handelSearchChanged = (e) => {
    setSearch(e.target.value);
  }
  return (
    <div>
      <label>Search:{search}</label>
      <input onChange = {() => handelSearchChanged()} placeholder ="seach..." name ="search" />
    <ul>
      {data.map(item => (
        <li key={item.objectID}>
          {/* <a href={item.url}>{item.capital}</a><br/> */}
          {item.name}<br/>
          {item.capital}<br/>
          {item.area}<br/>
          {item.region}<br/>
          {item.flag}
        </li>
      ))} 
    </ul>
    </div>

  );
}
export default App;
