import React, { Fragment, useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState('');
  const [query, setQuery] = useState({languages: []});


  useEffect(() => {
    console.log('Effect');
    axios
    .get('https://restcountries.eu/rest/v2/all')
    .then(response => {
      console.log('Promise fulfiled', response);
      setQuery(response.query);
    }) 
    .catch(error => {
      console.log(error.response);
    });

  }, [query]);

  useEffect(() => {
    // console.log('Effect');
    axios
    .get(`https://restcountries.eu/rest/v2/capital/${search}`)
    // .get(`https://restcountries.eu/rest/v2/currency/${search}`)
    .then(response => {
      console.log('Promise fulfiled', response);
      setData(response.data);
    }) 
    .catch(error => {
      console.log(error.response);
    });

  }, [search]);

  // const handelSearchChanged = (e) => {
  //   setSearch(e.target.value);
  // }
  return (
    <Fragment>
      Search:<input type = 'text' value = {search} onChange = {(e) => setSearch(e.target.value)} placeholder = "Capital ..."/>

      {/* <button onClick = {setSearch(search)}>Search</button> */}
    <ul>
      {data.map(item => (
        <li key={item.name}>
          {/* <a href={item.url}>{item.capital}</a><br/> */}
          Name: {item.name}<br/>
          Capital: {item.capital}<br/>
          Population: {item.population}<br/>
          Area: {item.area}<br/>
          Region: {item.region}<br/>
          {/* {item.currencies.code}*/}

          <img src = {item.flag} alt = "flag" width = "200" height = "140" border = "1px solid #000000" />
        </li>
      ))} 
    </ul>
    <ul>
      {query.langauges.map(lan => (
        <li key = {lan.name}>
          Languages: {lan.name}
        </li>
      ))}
    </ul>
    </Fragment>

  );
}
export default App;


