import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [data, setData] = useState({currencies:[]});
  const [search, setSearch] = useState('');


  useEffect(() => {
    console.log('Effect');
    axios
    .get('https://restcountries.eu/rest/v2/all')
    .then(response => {
      console.log('Promise fulfiled', response);
      setData(response.data);
    }) 
    .catch(error => {
      console.log(error.response);
    });

  }, []);

  useEffect(() => {
    console.log('Effect');
    axios
    // .get(`https://restcountries.eu/rest/v2/capital/${search}`)
    .get(`https://restcountries.eu/rest/v2/currency/${search}`)
    .then(response => {
      console.log('Promise fulfiled', response);
      setData(response.data.currencies);
    }) 
    .catch(error => {
      console.log(error.response);
    });

  }, [search]);

  // const handelSearchChanged = (e) => {
  //   setSearch(e.target.value);
  // }
  return (
    <div>
      <input type = 'text' value = {search} onChange = {(e) => setSearch(e.target.value)}/>
      {/* <button onClick = {setSearch(search)}>Search</button> */}
    <ul>
      {data.map(item => (
        <li key={item.name}>
          {/* <a href={item.url}>{item.capital}</a><br/> */}
          {item.name}<br/>
          {item.capital}<br/>
          {item.area}<br/>
          {item.region}<br/>
          {item.currencies.name}
        </li>
      ))} 
    </ul>
    </div>

  );
}
export default App;


