import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState('');


  useEffect(() => {
    console.log('Effect');
    axios
    .get('https://restcountries.eu/rest/v2/all')
    .then(response => {
      console.log('Promise fulfiled', response);
      setData(response.data);
    }) 
    .catch(error => {
      console.log(error.response);
    });

  }, []);

  useEffect(() => {
    console.log('Effect');
    axios
    .get(`https://restcountries.eu/rest/v2/capital/${search}`)
    // .get(`https://restcountries.eu/rest/v2/currency/${search}`)
    .then(response => {
      console.log('Promise fulfiled', response);
      setData(response.data);
    }) 
    .catch(error => {
      console.log(error.response);
    });

  }, [search]);

  // const handelSearchChanged = (e) => {
  //   setSearch(e.target.value);
  // }
  return (
    <div>
      <h5>Search:<input type = 'text' value = {search} onChange = {(e) => setSearch(e.target.value)} placeholder = "Capital ..."/></h5>

      {/* <button onClick = {setSearch(search)}>Search</button> */}
    <ul>
      {data.map(item => (
        <li key={item.name}>
          {/* <a href={item.url}>{item.capital}</a><br/> */}
          <td>Name: {item.name}</td><br/>
          <td>Capital: {item.capital}</td><br/>
          <td>Population: {item.population}</td>
          <td>Area: {item.area}</td><br/>
          <td>Region: {item.region}</td><br/>
          {/* <td>{item.currencies.code}</td> */}
          <td>Flag:<img src = {item.flag} alt = "flag" width = "200" height = "140" border = "1px solid #000000" /></td> 
        </li>
      ))} 
    </ul>
    </div>

  );
}
export default App;


