import React, { Fragment, useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState('');
  const [shown, setShown] = useState({});
  const [lan, setLan] = useState({languages:[]});
  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState(false)


  // useEffect(() => {
  //   console.log('Effect');
  //   axios
  //   .get('https://restcountries.eu/rest/v2/all')
  //   //.get ('api.openweathermap.org/data/2.5/weather?q={city name},{country code}')
  //   .then(response => {
  //     console.log('Promise fulfiled', response);
  //     setData(response.data);
  //   }) 
  //   .catch(error => {
  //     console.log(error.response);
  //   });

  // }, []);

  useEffect(() => {
    // console.log('Effect');
    setIsLoading(false); 
    setIsError(false);
    axios
    // .get(`https://restcountries.eu/rest/v2/capital/${search}`)
    .get(`https://restcountries.eu/rest/v2/name/${search}`) 
    // .get(`https://restcountries.eu/rest/v2/currency/${search}`)
    .then(response => {
      console.log('Promise fulfiled', response);
      setData(response.data);
      setLan({languages:response.data});

    }) 
    .catch(error => {
      console.log(error.response);
      setIsError(true);
    });

  }, [search]);

  const handelSearchChanged = (e) => {
    e.preventDefault();
    setSearch(e.target.value);

    if (e.target.value.length < 3){
     //e.target.value.innerHTML = "max value 4";
      document.getElementById("text").style.visibility = "visible";
    }else{
      document.getElementById("text").style.visibility = "hidden";
    }
  }


  const toggleShown = (name) => {
    setShown(prevShown => ({
      ...prevShown,
      [name]:!prevShown[name]
    }));
  }

  const labelStyle = {
    visibility: 'hidden',
    color: 'red'
  };


  return (
    <Fragment>
      {isError || <div>something went wrong...</div>}
      {isLoading ? (<div>Loading ...</div>): (
      <div>
        Find Countries: <input type = 'text'  value = {search} onChange = {handelSearchChanged} placeholder = "Search Countries ..."/>
        <label id = "text" style={labelStyle}>Too many matches, Specify another letter</label>
      </div> 
      )} 

     <div> 
     {data.map(item => (
        <ul key={item.name}>
          {item.name ? (<button id = "show" onClick ={() => toggleShown(item.name)}>Show</button>): null}<br/>
          <div><strong>{item.name}</strong></div>
          {shown[item.name] ? 
          <div>
          Capital:{item.capital}<br/>
          Population:{item.population}<br/>
          Area:{item.area}<br/>
          Region:{item.region}<br/>
          {lan.languages.map(it => (
            <ul key = {it.name}>
              Languages:{it.name}
            </ul>
          ))}
          Languages:{item.currencies.name}<br/>
          <img src = {item.flag} alt = "flag" width = "150" height = "90" border = "1px solid #000000" />
          </div>
          :null}
        </ul>
      ))}
      </div>
    </Fragment>
  );
}
export default App;
